examples.query:
mutation {
  createDashboard(
    input: {
      searchDomainName: "example-repo"
      name: "API Created Dashboard"
      labels: []
      widgets: [
        {
          id: "<WIDGET_ID>"
          x: 0
          y: 0
          width: 4
          height: 4
          title: "Methods Widget"
          queryOptions: {
            queryString: "status=200 | groupBy(method)"
            widgetType: "pie-chart"
          }
        }
      ]
      description: "Dashboard created via GraphQL"
    }
  ) {
    dashboard { id name }
  }
}
