examples.query:
mutation {
  createScheduledSearch(
    input: {
      searchDomainName: "example-repo"
      name: "Daily Error Rate Alert"
      queryString: "#level=ERROR | timechart(function=count(), span=1h)"
      cronSchedule: "0 0 * * *"
      enabled: true
    }
  ) {
    scheduledSearch { id name enabled }
  }
}
